package com.syntax.class101;

public class NonPrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//storing and representing numeric values;
		byte box1=127;
		short box2=32767;
		int box3=100000; //popular
		long box4=100000000; //credit cards
		
		//storing and representing decimal values;
		float variable1      =        1.99F;
		
		double variable2     =        99.99; //popular
		
		//storing single characters;
		char container='a';
		char dollar='$';
			
		//storing yes and no values;
		boolean hot=true;
		boolean tired=false;
		
		System.out.println(variable2);      //printing value of variable
		System.out.println("variable2");    //printing text b/c of quotes
		
		System.out.println(tired); 
		//System.out.println(Variable3); -variable does not exist
	
	}

}
